import React, { Component } from 'react';
import { Container,Form,Button } from 'react-bootstrap';
import AddQuiz from './AddQuiz';
import GiveQuiz from './GiveQuiz';
class Quiz extends Component {
    state={
        quizcode:''
    }
    
    render() {
        
        return (
            <Container>
                

                <React.Fragment>
                    <br />
                    <br />
                    <br />
                    <Form.Group controlId="formGridAddress1">
                        <center><Form.Label>Create Quiz Code</Form.Label></center>
                        <Form.Control placeholder="Code" value={this.state.quizcode} onChange={(e)=>this.setState({quizcode:e.target.value})} />
                        <br/>
                        <Button variant="primary">Generate</Button> &nbsq;
                        <Button variant="success">Give Quiz</Button>
                    </Form.Group>
                    <AddQuiz />
                    <GiveQuiz />
                </React.Fragment>
            </Container>
        );
    }
}

export default Quiz;
